import React from 'react';
import ReactDOM from 'react-dom';
import Card from './Card';
import Button from './Button';
import classes from './ErrorModal.module.css';

const Backdrop = props => {
  return (
    <div 
    className={classes.backdrop} 
    onClick={props.onConfirm} 
    />
  )
};

const ModalOverlay = props => {
  return (
    <Card className={classes.modal}>
      <header className={classes.header}>
        <h2>{props.title}</h2>
      </header>
      <div className={classes.content}>
        <p>{props.message}</p>
      </div>
      <footer className={classes.actions}>
        <Button onClick={props.onConfirm}>Okay</Button>
      </footer>
    </Card>
  )
};

const ErrorModal = (props) => {
  return (
    <React.Fragment>
      {ReactDOM.createPortal(
      <Backdrop onConfirm={props.onConfirm}
      />, 
      document.getElementById('backdrop-root')
      )};
      {ReactDOM.createPortal(
      <ModalOverlay onConfirm={props.onConfirm} 
      title={props.title} message={props.message} 
      />, 
      document.getElementById('overlay-root')
      )};
    </React.Fragment>
  );
};

export default ErrorModal;

//createPortal() method need two argument
//first one is React node should be rendered and seconde one is container in the real DOM(in index.js, index.html)
//now we can render backdrop, modaloverlay in existing application(ErrorModal)
//simply say, secode one is where DOM node reder this html content
